//manifest.json

  “清单版本”：3，
  “名称”：“PDF Downloader_Test4”，
  “版本”：“1.0”，
  &quot;description&quot;: &quot;从 Google 搜索结果下载 PDF 链接&quot;,
  “权限”：[“存储”、“activeTab”、“下载”]，
  “背景”：{
    “service_worker”：“background.js”
  },
  “内容脚本”：[
    {
      “匹配”：[“https://www.google.com/search?*”],
      “js”: [“内容.js”]
    }
  ],
  “行动”：{
    “default_popup”：“popup.html”，
