class ImagePairDataset(Dataset): def __init__(self, data_save_folder, dataset_name, num_samples, transform=None): &quot;&quot;&quot; Args: data_save_folder (str)ï¼šåŒ…å«æ•°æ®æ–‡ä»¶çš„æ–‡ä»¶å¤¹è·¯å¾„ã€‚ dataset_name (str)ï¼šè¡¨ç¤ºæ•°æ®é›†æ‹†åˆ†çš„â€œtrainâ€ã€â€œvalâ€æˆ–â€œtestâ€ä¹‹ä¸€ã€‚ num_samples (int)ï¼šæ•°æ®é›†æ‹†åˆ†ä¸­çš„æ ·æœ¬æ•°ã€‚ (train: 3000000, val: 10000, test: 10000) transform (å¯è°ƒç”¨ï¼Œå¯é€‰)ï¼šåº”ç”¨äºå›¾åƒå¼ é‡çš„å¯é€‰å˜æ¢ã€‚ &quot;&quot;&quot; self.data_save_folder = data_save_folder self.dataset_name = dataset_name self.num_samples = num_samples self.transform = transform
def __len__(self): return self.num_samples
def __getitem__(self, idx):
# æ ¹æ® idx æ„å»ºæ–‡ä»¶è·¯å¾„ A_image_path = f&quot;{self.data_save_folder}/{self.dataset_name}_A_images_{idx}.pt&quot; B_image_path = f&quot;{self.data_save_folder}/{self.dataset_name}_B_images_{idx}.pt&quot; label_path = f&quot;{self.data_save_folder}/{self.dataset_name}_labels_{idx}.pt&quot;
# ä»æ–‡ä»¶è·¯å¾„åŠ è½½å¼ é‡ A_image = torch.load(A_image_path) B_image = torch.load(B_image_path) label = torch.load(label_path)
# å¦‚æœå¯ç”¨ï¼Œåˆ™åº”ç”¨è½¬æ¢ if self.transform: A_image = self.transform(A_image) B_image = self.transform(B_image)
return A_image, B_image, label
class ImagePairDataModule(pl.LightningDataModule):
def __init__(self, data_save_folder, train_samples, val_samples, test_samples, batch_size=32, num_workers=4): super().__init__() self.data_save_folder = data_save_folder self.train_samples = train_samples self.val_samples = val_samples self.test_samples = test_samples self.batch_size = batch_size self.num_workers = num_workers self.train_transform = augmentation self.eval_transform = normalize # ä»…å¯¹éªŒè¯å’Œæµ‹è¯•è¿›è¡Œæ ‡å‡†åŒ–
def setup(self, stage=None):
self.train_dataset = ImagePairDataset(self.data_save_folder, &#39;train&#39;, self.train_samples, transform=self.train_transform) self.val_dataset = ImagePairDataset(self.data_save_folder, &#39;val&#39;, self.val_samples, transform=self.eval_transform) self.test_dataset = ImagePairDataset(self.data_save_folder, &#39;test&#39;, self.test_samples, transform=self.eval_transform)
def train_dataloader(self): #prefetch_factor=1, , persistent_workers=True return DataLoader(self.train_dataset, batch_size=self.batch_size, shuffle=True, num_workers=self.num_workers)
def val_dataloader(self): return DataLoader(self.val_dataset, batch_size=self.batch_size, shuffle=False, num_workers=self.num_workers)
