 private readonly MLContext _mlContext;
        // 只读 bir alanın sadece okunabilir olduğunu belirtir。
        私有只读 PredictionEngine _预测引擎；

        string outputCoulmnName = &quot;功能&quot;;
        公共决策树控制器（）
        {
            _mlContext = 新的 MLContext(种子: 0);

            // 完全模型化。

            var traininData = 新[]
            {
                新的 PredictionModel { 特征 1 = 1，特征 2 = 1，预测=“A sınıfı”}，
                新的 PredictionModel { 特征 1 = 2，特征 2 = 2，预测=“B sınıfı”}，
                新的 PredictionModel { 特征 1 = 3，特征 2 = 3，预测 = “A sınıfı”}，
                新的 PredictionModel { 特征 1 = 4，特征 2 = 4，预测 = “B sınıfı”}
            };
            // Verileri algoritmaya hazırlama
            var dataView = _mlContext.Data.LoadFromEnumerable(traininData);



            var pipeline = _mlContext.Transforms.Conversion.MapValueToKey(“标签”, “预测”)
                .Append(_mlContext.Transforms.Concatenate(outputCoulmnName, new string[] { &quot;Feature1&quot;, &quot;Feature2&quot; }))
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“预测”, “预测”))
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“标签”, “标签”))
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“功能”, “功能”));

            // DT 是 akışının belirtilmesi

            var trainer = _mlContext.MulticlassClassification.Trainers.SdcaNonCalibrate();

            // 随机双坐标上升（未校准） = ML.NET kütüphanesinde
            // 解决算法中的问题
            var traininPipeline = pipeline.Append(_mlContext.Transforms.Conversion.MapValueToKey(“预测”, “预测”))
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“标签”, “标签”))
                .追加（训练器）
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“标签”, “预测”))
                .Append(_mlContext.Transforms.Conversion.MapValueToKey(“预测”, “预测”));


            // 模型hangi verilerle eğitilmesi gerektiğini belirtip eğitilmiş modeli kaydetme işlemi



            var TrainingModel = traininPipeline.Fit(dataView);



