class ZeroLayerTransformers(nn.Module): def __init__(self): super().__init__() self.W_e = nn.Embedding(vocab_size, embedding_size) self.W_U = nn.Embedding(vocab_size, embedding_size)
def forward(self, tokens, target=None): x = self.W_e(tokens) logits = torch.matmul(x, self.W_U.weight.T)
if goals is None: loss = None else: batch_size, time, _ = logits.shape logits = logits.view(batch_size * time, vocab_size) target = target.view(batch_size * time) loss = F.cross_entropy(logits, target) return logits, loss
