类感知器{
  构造函数（数字）{
    this.权重 = []
    这个.lr = 0.1

    for (var i = 0; i &lt; num; i++) {
      this.weights.push(Math.random() * 2 - 1)
    }
  }

  猜测（输入）{
    变量总和 = 0
    for (var i = 0; i &lt; this.weights.length; i++) {
      sum += this.weights[i] * 输入[i]
    }
    var 输出 = 符号（总和）
    返回输出
  }

  训练（输入，目标）{
    var 猜测 = this.guess(输入)
    var 错误 = 目标 - 猜测

    for (var i = 0; i &lt; this.weights.length; i++) {
      this.weights[i] += 错误 * 输入[i] * this.lr
    }
  }
}

