if request.method == &#39;POST&#39;:
return render_template(&#39;index.html&#39;, label=&#39;3&#39;)

if __name__ == &#39;__main__&#39;:
# APP 运行时加载模型 ####
model = joblib.load(&#39;demo_model.pkl&#39;)
