class GCN(torch.nn.Module): def __init__(self, hidden_â€‹â€‹size=32): super(GCN, self).__init__() self.conv1 = GCNConv(tree_dataset.num_node_features, hidden_â€‹â€‹size) self.conv2 = GCNConv(hidden_â€‹â€‹size, hidden_â€‹â€‹size) self.linear = Linear(hidden_â€‹â€‹size, tree_dataset.num_classes)
def forward(self, x, edge_index, edge_attr, batch): # 1. è·å–èŠ‚ç‚¹åµŒå…¥ x = self.conv1(x, edge_index, edge_attr) x = x.relu() x = self.conv2(x, edge_index, edge_attr)
# 2. è¯»å‡ºå±‚ x = global_mean_pool(x, batch) # [batch_size, hidden_â€‹â€‹channels]
# 3. åº”ç”¨æœ€ç»ˆåˆ†ç±»å™¨ x = F.dropout(x, p=0.5, training=self.training) x = self.linear(x)
return x
model = GCN(hidden_â€‹â€‹size=32) optimizer = torch.optim.Adam(model.parameters(), lr=0.01) criterion = torch.nn.CrossEntropyLoss() train_loader = DataLoader(tree_dataset, batch_size=64, shuffle=True) print(model)
def train(): model.train()
lost_all = 0 for data in train_loader: optimizer.zero_grad() # æ¸…é™¤æ¢¯åº¦ã€‚ out = model(data.x, data.edge_index, data.edge_attr, data.batch) # æ‰§è¡Œä¸€æ¬¡å‰å‘ä¼ é€’ã€‚ loss = criterion(out, data.y) # è®¡ç®—æŸå¤±ã€‚ loss.backward() # å¾—å‡ºæ¢¯åº¦ã€‚ lost_all += loss.item() * data.num_graphs optimizer.step() # æ ¹æ®æ¢¯åº¦æ›´æ–°å‚æ•°ã€‚
return lost_all / len(train_loader.dataset)
def test(loader): model.eval()
correct = 0 for data in loader: # åœ¨è®­ç»ƒ/æµ‹è¯•æ•°æ®é›†ä¸Šåˆ†æ‰¹è¿­ä»£ã€‚ out = model(data.x, data.edge_index, data.edge_attr, data.batch) pred = out.argmax(dim=1) # ä½¿ç”¨æ¦‚ç‡æœ€é«˜çš„ç±»ã€‚ correct += int((pred == data.y).sum()) # å¯¹ç…§çœŸå®æ ‡ç­¾è¿›è¡Œæ£€æŸ¥ã€‚ return correct / len(loader.dataset) # å¾—å‡ºæ­£ç¡®é¢„æµ‹çš„æ¯”ä¾‹ã€‚
for epoch in range(1, 20): loss = train() train_acc = test(train_loader) # test_acc = test(test_loader) print(f&#39;Epoch: {epoch:03d}, Train Acc: {train_acc:.4f}, Loss: {loss:.4f}&#39;)
