public static Classifier create(
AssetManager assetManager,
String modelFilename,
String[] labels,
int inputSize,
int imageMean,
float imageStd,
String inputName,
String outputName) {
final TensorFlowImageClassifier c = new TensorFlowImageClassifier();
c.inputName = inputName;
c.outputName = outputName;

// å°†æ ‡ç­¾åç§°è¯»å…¥å†…å­˜ã€‚
Collections.addAll(c.labels, labels);

c.inferenceInterface = new TensorFlowInferenceInterface(assetManager, modelFilename);

// è¾“å‡ºçš„å½¢çŠ¶ä¸º [N, NUM_CLASSES]ï¼Œå…¶ä¸­ N æ˜¯æ‰¹æ¬¡å¤§å°ã€‚
final Operation operation = c.inferenceInterface.graphOperation(outputName);
final int numClasses = (int) operation.output(0).shape().size(1);

// ç†æƒ³æƒ…å†µä¸‹ï¼Œå¯ä»¥ä»è¾“å…¥æ“ä½œçš„å½¢çŠ¶ä¸­æ£€ç´¢ inputSizeã€‚å”‰ï¼Œ
// é€šå¸¸ä½¿ç”¨çš„ graphdef ä¸­è¾“å…¥çš„å ä½ç¬¦èŠ‚ç‚¹æœªæŒ‡å®šå½¢çŠ¶ï¼Œå› æ­¤å®ƒ
// å¿…é¡»ä½œä¸ºå‚æ•°ä¼ å…¥ã€‚
c.inputSize = inputSize;
c.imageMean = imageMean;
c.imageStd = imageStd;

// é¢„åˆ†é…ç¼“å†²åŒºã€‚
c.outputNames = new String[]{outputName};
c.intValues = new int[inputSize * inputSize];
c.floatValues = new float[inputSize * inputSize * 3];
